import React, { useState, useEffect, Fragment } from 'react';
import uuid from 'uuid/v1';

import { Container, NewNote, NewNoteInput, NoteList } from './styles.js';
import Header from '../../components/Header';
import Note from '../../components/Note';
import Error from '../../components/Error';
import NavigationDrawer from '../../components/NavigationDrawer';
import NoteService from '../../services/NoteService';

export default function App({ className }) {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [loading, setLoading] = useState(false);
  const [reloadHasError, setreloadHasError] = useState(false);
  const [saveHasError, setSaveHasError] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    handleReload();
  }, []);

  const handleAddNote = text => {
    const newNotes = [...notes, { id: uuid(), text }];
    setNotes(newNotes);
    handleSaveAll(newNotes);
  };

  const handleMove = (direction, index) => {
    const newNotes = notes.slice();
    const removedNote = newNotes.splice(index, 1)[0];

    if (direction === 'up') {
      newNotes.splice(index - 1, 0, removedNote);
    } else {
      newNotes.splice(index + 1, 0, removedNote);
    }

    setNotes(newNotes);
    handleSaveAll(newNotes);
  };

  const handleDelete = id => {
    const newNotes = notes.slice();
    const index = newNotes.findIndex(note => note.id === id);
    newNotes.splice(index, 1);

    setNotes(newNotes);
    handleSaveAll(newNotes);
  };
  const handleSave = (id, text) => {
    let newNotes = notes.slice();
    const index = newNotes.findIndex(note => note.id === id);
    newNotes[index].text = text;

    setNotes(newNotes);
    handleSaveAll(newNotes);
  };
  const handleReload = () => {
    setreloadHasError(false);
    setLoading(true);
    NoteService.load()
      .then(data => {
        setNotes(data);
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
        setreloadHasError(true);
      });
  };
  const handleSaveAll = (nNotes = []) => {
    setSaveHasError(false);
    setLoading(true);
    const newNotes = nNotes ? nNotes : notes;
    NoteService.save(newNotes)
      .then(() => {
        setLoading(false);
      })
      .catch(() => {
        setSaveHasError(true);
        setLoading(false);
      });
  };

  const handleOpenMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div>
      <Header
        isLoading={loading}
        saveHasError={saveHasError}
        retrySave={() => handleSaveAll(notes)}
        onOpenMenu={handleOpenMenu}
      />
      <Container>
        {reloadHasError ? (
          <Error onRetry={handleReload} />
        ) : (
          <Fragment>
            <NewNote>
              <NewNoteInput
                type="text"
                placeholder="Digite sua nota aqui..."
                value={newNote}
                onChange={change => setNewNote(change.target.value)}
                onKeyPress={event => {
                  if (event.key === 'Enter') {
                    handleAddNote(event.target.value);
                    setNewNote('');
                  }
                }}
              />
            </NewNote>
            <NoteList>
              {notes.map((note, index) => (
                <Note
                  key={uuid()}
                  note={note}
                  handleDelete={handleDelete}
                  handleMove={handleMove}
                  handleSave={handleSave}
                  index={index}
                  total={notes.length}
                />
              ))}
            </NoteList>
          </Fragment>
        )}
      </Container>
      <NavigationDrawer isOpen={isMenuOpen} onCloseMenu={handleOpenMenu} />
    </div>
  );
}
